INSERT INTO `craft_matrixcontent_press` (`id`, `elementId`, `locale`, `field_pressPiece_publication`, `field_pressPiece_articleTitle`, `field_pressPiece_description`, `field_pressPiece_seeMoreLink`, `dateCreated`, `dateUpdated`, `uid`) VALUES
 ('1', '27', 'en_us', 'CBS This Morning', 'Antarctica Climbers', 'Hidden deep within Antarctica\'s icy terrain lie half-a-dozen mountains that few climbers have summited. One group decided to scale all six, something no one had ever done before. They completed the expedition in less than 10 days. Conrad Anker, Cedar Wright and Alex Honnold, three of the all-star North Face climbers who recently returned from that journey, join \"CBS This Morning\" to discuss their experiences.', 'http://www.cbs.com/shows/cbs_this_morning/video/z7irE2yRGTgDilzZYhB0qCGeddHSmVEe/climbers-on-scaling-antarctica-mountains-spirit-of-exploration/', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '51a31822-2f06-4089-828e-81e390b13a08'),
 ('2', '28', 'en_us', 'National Geographic', 'Expedition Antarctica', 'A team of six accomplished American climbers are currently exploring Queen Maud Land, an otherworldly territory of Antarctica. Facing extreme and challenging conditions, the goal of this month-long expedition is to reach as many summits of the Wolf’s Jaw massif as possible.', 'https://www.nationalgeographic.com/adventure/destinations/antarctica/expedition-antarctica/ ', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '14f00b13-0017-4030-90f1-49298c1fbe50'),
 ('3', '29', 'en_us', 'Men\'s Journal', 'Life Advice from Conrad Anker', 'A collection of life\'s pressing questions, answered candidly by Conrad Anker.', 'https://www.mensjournal.com/adventure/life-advice-from-conrad-anker-w201283', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '377886e2-4605-4b34-8faa-97c3f86bd434'),
 ('4', '30', 'en_us', 'Outside Magazine', 'An Icy Michigan Landscape Becomes Conrad Anker’s Playground', 'A collection of images and captured during the filming of National Parks Adventure.', 'https://www.outsideonline.com/2066486/icy-michigan-landscape-becomes-conrad-ankers-playground ', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '8e012a68-3e6c-40fb-99db-92dfb9e3158b'),
 ('5', '31', 'en_us', 'Gear Junkie', 'Hyalite Alpine: Climbing Ice With Conrad Anker', 'We’re in Hyalite Canyon, an alpine wonderland above Anker’s hometown of Bozeman, Mont., looking up at a dark corner of dripping stone and ice. ', 'https://gearjunkie.com/featured/ice-climbing-conrad-anker-hyalite', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '4e919ad0-602f-4223-ba5a-1dade2825661'),
 ('6', '32', 'en_us', 'Outside Magazine', 'How Conrad Risked it All to Chase a Dream', 'After winning the coveted audience award at Sundance, the documentary \'Meru\' is getting a nationwide theatrical release next month. How did a climbing movie break through with mainstream audiences? Credit the incredible story at its center: a tale of tragedy, family, friendship, risk, and the redemptive power of suffering.', 'https://www.outsideonline.com/1990471/meru-conrad-anker-profile', '2018-03-01 01:51:04', '2018-03-01 01:51:04', '24b3991c-3a72-4a15-8b13-80efb12caa2c');



